const express = function() {
  const app = {
    handlers: {},
    use: () => {},
    post: function(path, handler) {
      this.handlers['POST ' + path] = handler;
    },
    listen: (port, cb) => {
      if(cb) cb();
      return { address: () => ({ port }), close: () => {} };
    }
  };
  return app;
};
express.json = () => {};
module.exports = express;
